import './App.css';
import HeroImage from "./pictures/MagdaHero.jpg"// with import
import {Parallax,ParallaxLayer} from '@react-spring/parallax'
import { useTrail,animated } from '@react-spring/web';


function App() {
  return (
    <Parallax pages={2}>
          <ParallaxLayer offset={0.25} speed={2}>
            <HeroText></HeroText>
          </ParallaxLayer>

          <ParallaxLayer className='bg-slate-700' speed={0.2} offset={0.5}>
            <HeroImageDisplay></HeroImageDisplay>
           </ParallaxLayer>
           <ParallaxLayer className='bg-red-400' offset={0.75}>

           </ParallaxLayer>


    </Parallax>
  );

}
function NavBar(){
      return(
        <div>
          <nav>

          </nav>
        </div>
      )
}


function HeroImageDisplay(){
  const [trails, api] = useTrail(1, () => ({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { mass: 1, tension: 200, friction: 150 }
  }));

        return(
          <div>
          {trails.map(()=>(
            <animated.div>
              <div className="flex items-center justify-center">
                <img src={HeroImage} className="w-56 h-auto" alt="HeroImage"></img>
              </div>
            </animated.div>

            )
          )
          }
          </div>
        )

}


  function HeroText() {
    const [trails, api] = useTrail(1, () => ({
      from: { opacity: 0 },
      to: { opacity: 1 },
      config: { mass: 1, tension: 200, friction: 150 }
    }));

    return (
      <div>
        {trails.map(() => (
          <animated.div className="text-slate-700 text-[12rem] text-center font-CormorantGaramond">
            Magda Nemes
          </animated.div>
        ))}
      </div>
    );
  }





export default App;
