import './App.css';
import img1 from "./WhatsApp Image 2024-02-19 at 20.22.48.jpeg"// with import
import {Parallax,ParallaxLayer} from '@react-spring/parallax'
import { useTrail,animated } from '@react-spring/web';


function App() {
  return (
    <Parallax pages={2}>
          <ParallaxLayer offset={0.25} speed={2}>
            <h1 className="font-CormorantGaramond text-[200px] text-slate-700 text-center">Magda Nemes</h1>
          </ParallaxLayer>

          <ParallaxLayer speed={0.25}>
            <Overlay className="z-10"></Overlay>
           </ParallaxLayer>


    </Parallax>
  );

}
function Overlay(){
  return(
  <div className="absolute top-62 left-56 w-48 h-auto shadow-xl">
        <img src={img1} alt="painting"></img>

  </div>
  )

   function TrailText(){
    const [trails, api] = useTrail(
      2,
      () => ({
        from: { opacity: 0 },
        to: { opacity: 1 },
      }),
      []
    ) 
    return(
      <div>
        {trails.map(props=>(
          <animated.div className={"text-red-500"}>Hello World</animated.div>
        ))}
      </div>
    )

  }
}




export default App;
