import './App.css';
import HeroImage from "./pictures/MagdaHero.jpg"// with import
import {Parallax,ParallaxLayer} from '@react-spring/parallax'
import { useTrail,animated } from '@react-spring/web';


function App() {
  return (
    <Parallax pages={2}>
          <ParallaxLayer offset={0.25} speed={2}>
            <HeroText></HeroText>
          </ParallaxLayer>

          <ParallaxLayer speed={0.25}>
            <HeroImageDisplay></HeroImageDisplay>
           </ParallaxLayer>


    </Parallax>
  );

}
function NavBar(){
      return(
        <div>
          <nav>

          </nav>
        </div>
      )
}


function HeroImageDisplay(){
        return(
          <div>
            <img src={HeroImage} alt="HeroImage"></img>
          </div>
        )

}


  function HeroText() {
    const [trails, api] = useTrail(1, () => ({
      from: { opacity: 0 },
      to: { opacity: 1 },
      config: { mass: 1, tension: 200, friction: 150 }
    }));

    return (
      <div>
        {trails.map((trail, index) => (
          <animated.div  style={trail} className="text-slate-700 text-[12rem] text-center font-CormorantGaramond">
            Magda Nemes
          </animated.div>
        ))}
      </div>
    );
  }





export default App;
