import './App.css';
import HeroImage from "./pictures/MagdaHero.jpg"// with import
import {Parallax,ParallaxLayer} from '@react-spring/parallax'
import { useTrail,animated } from '@react-spring/web';


function App() {
  return (
    <div className="bg-moon-mist-100 h-screen">
      <HeroText></HeroText>
    </div>
  );

}
function NavBar(){
      return(
        <div>
          <nav>

          </nav>
        </div>
      )
}


function HeroImageDisplay(){
  const [trails, api] = useTrail(1, () => ({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { mass: 1, tension: 200, friction: 150 }
  }));

        return(
          <div>
          {trails.map(()=>(
            <animated.div>

              <div >
                  <img src={HeroImage} className="w-56 h-auto rounded-full" alt="HeroImage"></img>
              </div>

            </animated.div>

            )
          )
          }
          </div>
        )

}


  function HeroText() {
    const [trails] = useTrail(1, () => ({
      from: { opacity: 0 },
      to: { opacity: 1 },
      config: { mass: 1, tension: 200, friction: 150 }
    }));

    return (
      <div>
        {trails.map((props) => (
          <animated.div style={props} className=" text-gun-powder-950 text-[12rem] text-center font-CormorantGaramond">
            Magda Nemes
          </animated.div>
        ))}
      </div>
    );
  }





export default App;
