import './App.css';
import img1 from "./WhatsApp Image 2024-02-19 at 20.22.48.jpeg"// with import
import {Parallax,ParallaxLayer} from '@react-spring/parallax'
import { useTrail,animated } from '@react-spring/web';


function App() {
  return (
    <Parallax pages={2}>
          <ParallaxLayer offset={0.25} speed={2}>
            <HeroText></HeroText>
          </ParallaxLayer>

          <ParallaxLayer speed={0.25}>
            <Overlay className="z-10"></Overlay>
           </ParallaxLayer>


    </Parallax>
  );

}
function Overlay(){
  return(
  <div className="absolute top-62 left-56 w-48 h-auto shadow-xl">
        <img src={img1} alt="painting"></img>

  </div>
  )
  }
  function HeroText() {
    const [trails, api] = useTrail(1, () => ({
      from: { opacity: 0 },
      to: { opacity: 1 },
      config: { mass: 1, tension: 200, friction: 100 }
    }));

    return (
      <div>
        {trails.map((trail, index) => (
          <animated.div  style={trail} className="text-slate-700 text-8xl text-center font-CormorantGaramond">
            Magda Nemes
          </animated.div>
        ))}
      </div>
    );
  }





export default App;
