import './App.css';
import HeroImage from "./pictures/MagdaHero.jpg"// with import
import {Parallax,ParallaxLayer} from '@react-spring/parallax'
import { useTrail,animated } from '@react-spring/web';


function App() {
  return (
    <div className='bg-moon-mist-100'>
      <div>
        <NavBar></NavBar>
      </div>
      <div className=" h-screen flex justify-center items-center">
        <HeroText></HeroText>
      </div>
    </div>
  );

}
function NavBar(){
      return(
        <div >
          <nav className="flex justify-between items-center">
          <a className='m-5'>Logo</a>
            <ul className='flex gap-2'>
              <li>About</li>
              <li>Projects</li>
            </ul>
            <a className='m-5'>Contact Me</a>
          </nav>
        </div>
      )
}


function HeroImageDisplay(){
  const [trails, api] = useTrail(1, () => ({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { mass: 1, tension: 300, friction: 150 }
  }));

        return(
          <div>
          {trails.map(()=>(
            <animated.div>

              <div >
                  <img src={HeroImage} className="w-56 h-auto rounded-full" alt="HeroImage"></img>
              </div>

            </animated.div>

            )
          )
          }
          </div>
        )

}


  function HeroText() {
    const [trails] = useTrail(1, () => ({
      from: { opacity: 0 },
      to: { opacity: 1 },
      config: { mass: 1, tension: 200, friction: 150 }
    }));

    return (
      <div>
        {trails.map((props) => (
          <animated.div style={props} className=" text-gun-powder-950 text-[10rem] text-center font-normal font-Montseratt">
            Magda Nemes
          </animated.div>
        ))}
      </div>
    );
  }





export default App;
